import React from 'react';
//import axios from 'axios';
import connect from 'react-redux';




const templatelst = [
    {
        "name": [
            "Natural One",
            "Techno 01",
            "Sporty"
        ],
        "versions": [
            "1.0.0",
            "1.0.1",
            "1.1.0",
            "2.0.0",
            "1.1.1",
            "2.0.1",
            "1.2.0",
            "1.2.1",
            "1.3.0"
        ]
    }
]

class AddDeployment extends React.Component
{
    constructor ()
    {
       super();

       this.state = {
           versionlst : templatelst[0],
           Url:"",
           TemplateName:"",
           version:""
       }

      this.onchange=this.onchange.bind(this);

    }


    onchange(e)
    {
        this.setState({[e.target.name]:e.target.value})
        console.log(this.state)

    }

    render()
    {

        return(
            <div>
             <table>
            <tbody>
                <tr>
                    <td>
                        <label>Url : </label>
                    </td>
                        <td>
                         <input type="text" name= "Url" width="120" height="40" onChange={this.onchange.bind()}/>
                </td>

                <td>
                        <label>Versions : </label>
                    </td>

                <td>
                 <select id = "versions" name="versions" width="120" height="40" onChange={this.onchange.bind()}>
                 {this.state.versionlst.versions.map((item) => <option key={item} value={item}>{item}</option>)}
                </select>
                </td>
                
                <td>
                        <label>Name : </label>
                    </td>
<td>

                <select id = "name" name="TemplateName" width="120" height="40" onChange={this.onchange.bind()}>
                 {this.state.versionlst.name.map((item) => <option key={item} value={item}>{item}</option>)}
                </select>
                </td>
                <td>
                    <input type="button" name="Add Deployment" width="120" value="Add Deployment" height="40"/>
                </td>
                </tr>
                </tbody>
                </table>
            </div>
        )

    }


}

export default AddDeployment;

