import axios from 'axios';
export const Success = val => {
    return { type: "ADD", payload: val };
  };

  export const Failure = val => {
    return { type: "ERR", payload: val };
  };


  export const Delete = val => {
    return { type: "DEL", payload: val };
  };

export const AddDeployment = (data) =>
{

 var request = {url:data.Url,templateName:data.TemplateName,version:data.versions,deployedAt:new Date().getDate()}

    return dispatch => {
        axios.post("http://localhost:8000/adddeployment",request)
          .then(res => {
            dispatch(Success(res.data));
           // console.log(res.data);
          })
          .catch(err => {
            console.log(err)
            dispatch(Failure(err.message));
            console.log(err);
          });
      };
};

export const RemoveDeployment = (value) =>
{

    return dispatch => {
        axios.post("http://localhost:8000/deletedeployment",{id:value})
          .then(res => {
            dispatch(Delete(res));
           
          })
          .catch(err => {
           
            dispatch(Failure(err.message));
          });
      };


}


export const LoadDeployment = () =>
{

 
    return dispatch => {
        axios.get("http://localhost:8000/getdeployments")
          .then(res => {
            dispatch(Delete(res.data.Data));
           // console.log(res.data.Data);
          })
          .catch(err => {
            console.log(err)
            dispatch(Failure(err.message));
          });
      };


}
