import axios from 'axios';
export const Success = val => {
    return { type: "ADD", value: val };
  };

  export const Failure = val => {
    return { type: "ERR", value: val };
  };


  export const Delete = val => {
    return { type: "DEL", value: val };
  };

export const AddDeployment = (data) =>
{

    return dispatch => {
        axios.post("http://localhost:8000/adddeployment",data)
          .then(res => {
            dispatch(Success(res.data));
          })
          .catch(err => {
            dispatch(Failure(err.message));
          });
      };
};

export const RemoveDeployment = (value) =>
{

    return dispatch => {
        axios.delete("http://localhost:8000/deletedeployment",value)
          .then(res => {
            dispatch(Delete(res.data));
          })
          .catch(err => {
            dispatch(Failure(err.message));
          });
      };


}


export const LoadDeployment = () =>
{

    return dispatch => {
        axios.get("http://localhost:8000/getdeployments")
          .then(res => {
            dispatch(Delete(res.data));
          })
          .catch(err => {
            dispatch(Failure(err.message));
          });
      };


}
